/* Local IMask placeholder for development (minimal shim). Replace with the official build if needed. */
(function(r){
  if (r && !r.IMask) {
    r.IMask = function(el, opts){
      var input = (typeof el === 'object' && el.nodeType === 1) ? el : null;
      var mask = (opts && opts.mask) || null;
      function clean(v){ return (v||'').toString(); }
      var api = {
        el: input,
        updateValue: function(v){ if(input){ input.value = clean(v); } },
        updateOptions: function(newOpts){ opts = Object.assign({}, opts||{}, newOpts||{}); },
        destroy: function(){ /* noop */ }
      };
      return api;
    };
  }
})(window);

